import React, { useState } from "react";
import classes from "./Delivery.module.css";
import back from "../../../img/Back.svg";
import { useDispatch, useSelector } from "react-redux";
import {
  addDelivery,
  addDeliveryTime,
} from "../../../redux/slices/basketSlice";
import { setTimesDelivery } from "../../../redux/slices/timesDelivery";
import { useForm } from "react-hook-form";

function Delivery(props) {
  const dispatch = useDispatch();
  const timeSlice = useSelector((state) => state.timesDelivery.times);
  const { register, handleSubmit } = useForm();
  const [timeClass, getTimeClass] = useState(classes.buttonTimes);
  const [timeClassNew, getTimeClassNew] = useState(classes.buttonTimes);
  const [activePickup, getActivePickup] = useState(classes.checkboxNotActive);
  const [activeDelivery, getActiveDelivery] = useState(classes.checkboxActive);
  const [selectedValue, setSelectedValue] = useState("Доставка");

  const handleRadioChange = (event) => {
    setSelectedValue(event.target.value); // Обновляем состояние при выборе радиокнопки
  };

  const timeOnClick = () => {
    getTimeClassNew(classes.buttonTimes);
    getTimeClass(classes.active);
  };

  const onSubmit = (data) => {
    console.log(data);
    dispatch(addDelivery(data));
  };
  const DELIVERY_TIME = [];

  const nameTime = (index) => {
    getTimeClassNew(classes.active);
    getTimeClass(classes.buttonTimes);
    dispatch(setTimesDelivery(index));
  };

  for (let i = 0; i < 24; i++) {
    if (i <= 9) {
      DELIVERY_TIME.push(`0${i}:00`);
      DELIVERY_TIME.push(`0${i}:30`);
    } else {
      DELIVERY_TIME.push(`${i}:00`);
      DELIVERY_TIME.push(`${i}:30`);
    }
  }

  const onChangeClick = (e) => {
    dispatch(addDeliveryTime(e.target.innerHTML));
  };

  const times = DELIVERY_TIME.map((time, index) => (
    <button
      key={index}
      onClick={(() => nameTime(index), onChangeClick)}
      className={
        Number(timeSlice) === index ? timeClassNew : classes.buttonTimes
      }
      {...register("time")}
    >
      {time}
    </button>
  ));

  const onClickPickup = () => {
    getActiveDelivery(classes.checkboxNotActive);
    getActivePickup(classes.checkboxActive);
  };
  const onClickDelivery = () => {
    getActivePickup(classes.checkboxNotActive);
    getActiveDelivery(classes.checkboxActive);
  };
  return (
    <div>
      <div onClick={props.back} className={classes.back}>
        <img src={back} alt="#" />
        <p>Назад</p>
      </div>
      <h2>Доставка</h2>
      <div>
        <form onSubmit={handleSubmit(onSubmit)}>
          <div className={classes.checkboxAdress}>
            <div className={classes.choiceDelivery}>
              <div>
                <input
                  id="myRadio_4"
                  type="radio"
                  name="delivery"
                  value="Доставка"
                  checked={selectedValue === "Доставка"}
                  onChange={handleRadioChange}
                  onClick={onClickDelivery}
                />
                <label htmlFor="myRadio_4">Доставка</label>
              </div>
              <div className={classes.deliv}>
                <input
                  id="myRadio_3"
                  type="radio"
                  name="delivery"
                  value="Самовывоз"
                  checked={selectedValue === "Самовывоз"}
                  onChange={handleRadioChange}
                  onClick={onClickPickup}
                />
                <label htmlFor="myRadio_3">Самовывоз</label>
              </div>
            </div>

            <div className={(classes.checkbox, activePickup)}>
              <h3>Выбирите один из вариантов:</h3>
              <div>
                <input
                  id="myRadio_1"
                  type="radio"
                  name="address"
                  value="г. Химки, пр-т Юбилейный, 51, к.1"
                  {...register("addressPoint")}
                />
                <label for="myRadio_1">г. Химки, пр-т Юбилейный, 51, к.1</label>
              </div>
              <div>
                <input
                  id="myRadio_2"
                  type="radio"
                  name="address"
                  value="г. Химки, пр-т Мельникова, 2Б, стр.1"
                  {...register("addressPoint")}
                />
                <label for="myRadio_2">
                  г. Химки, пр-т Мельникова, 2Б, стр.1
                </label>
              </div>
            </div>
            <div className={classes.inputAdress}>
              <div className={activeDelivery}>
                <label for="address">Доставить по адресу:</label>
                <input
                  type="text"
                  name="address"
                  placeholder="пр-т Мельникова, 2Б, стр.1"
                  {...register("address")}
                />
              </div>
              <div>
                <label for="phone">Телефон (через +7 без пробелов):</label>
                <input
                  type="tel"
                  name="phone"
                  placeholder="+79991110000"
                  pattern="\+7[0-9]{10}"
                  required
                  {...register("phone")}
                />
              </div>
              <div>
                <label for="addition">Дополнение к заказу:</label>
                <input
                  type="text"
                  name="Addition"
                  placeholder="Домофон не работает. код от домофона: 1111"
                  {...register("addition")}
                />
              </div>
            </div>
          </div>
          <h3 className={classes.TitleDelivery}>К какому времени привезти:</h3>
          <button
            onClick={(timeOnClick, onChangeClick)}
            className={timeClass}
            type="submit"
          >
            По готовности
          </button>
          <div className={classes.times}>{times}</div>
        </form>
      </div>
    </div>
  );
}

export default Delivery;
