import React from "react";
import styles from "./Navigation.module.css";

function Navigation(props) {
  // const [catigories, setCatigories] = React.useState(0);

  // const onSelectItem = (item) => {
  //   setCatigories(item);
  // };
  const navigation = props.navigation.map((item, index) => (
    <li
      key={item.id}
      className={item.id === index ? styles.active : " "}
      onClick={props.onClickCategories(item.id)}
    >
      {item.name}
    </li>
  ));

  return (
    <div className={styles.navigastion}>
      <ul>{navigation}</ul>
    </div>
  );
}

export default Navigation;
