import React from "react";
import Card from "../../UI/Card/Card";
import Styles from "./TaskInput.module.css";

function TaskInput(props) {
  const [title, setTitle] = React.useState("");
  const [text, setText] = React.useState("");
  const [time, setTime] = React.useState("");

  const addTitle = (event) => {
    setTitle(event.target.value);
  };
  const addText = (event) => {
    setText(event.target.value);
  };
  const addTime = (event) => {
    setTime(event.target.value);
  };

  const addTest = (event) => {
    event.preventDefault();
    const dateSet = {
      id: Math.random().toString(),
      title: title,
      text: text,
      time: time,
    };
    props.addInputList(dateSet);
  };

  return (
    <div className={Styles.container}>
      <Card>
        <form onSubmit={addTest}>
          <label htmlFor="">Название задачи</label>
          <input type="text" onChange={addTitle} value={title} />
          <label htmlFor="">Описание</label>
          <input type="textarea" onChange={addText} value={text} />
          <label htmlFor="">Время</label>
          <input type="date" onChange={addTime} value={time} />
          <button type="submit">Добавить</button>
        </form>
      </Card>
    </div>
  );
}

export default TaskInput;
