import { createSlice } from "@reduxjs/toolkit";

const initialState = {
  totalPrice: 0,
  totalAmount: 0,
  items: [],
};

export const basketSlice = createSlice({
  name: "basket",
  initialState,
  reducers: {
    addItems(state, action) {
      const findItem = state.items.find((obj) => obj.id === action.payload.id);
      if (findItem) {
        findItem.amount++;
      } else {
        state.items.push({ ...action.payload, amount: 1 });
      }
      state.totalPrice = state.items.reduce((sum, obj) => {
        return obj.price * obj.amount + sum;
      }, 0);
      state.totalAmount = state.items.reduce((sum, obj) => {
        return obj.amount + sum;
      }, 0);
    },

    setBasketItemPlus(state, action) {
      const findItemPlus = state.items.find((obj) => obj.id === action.payload);
      if (findItemPlus) {
        state.totalAmount++;
        findItemPlus.amount++;
      }
      state.totalPrice = state.items.reduce((sum, obj) => {
        return obj.price * obj.amount + sum;
      }, 0);
    },
    setBasketItemMinus(state, action) {
      const findItemMinus = state.items.find(
        (obj) => obj.id === action.payload
      );
      if (findItemMinus) {
        state.totalAmount--;
        findItemMinus.amount--;
      }

      state.totalPrice = state.items.reduce((sum, obj) => {
        return obj.price * obj.amount - sum;
        //debugger;
      }, state.totalPrice);
      //debugger;
    },
    removeItems(state, action) {
      state.items = state.items.filter((obj) => obj.id !== action.payload);
      // state.totalPrice = state.items.reduce((obj) => {
      //   return state.totalPrice - obj.price;
      // });
      state.totalAmount--;
    },
  },
});

export const { setBasketItemPlus, setBasketItemMinus, addItems, removeItems } =
  basketSlice.actions;

export default basketSlice.reducer;
