import React from "react";

import styles from "./MealItemForm.module.css";

function CardButton(props) {
  const [isAmountValid, setIsAmountValid] = React.useState(true);
  const [count, setCount] = React.useState(null);
  //const amountInputRef = React.useRef("");

  React.useEffect(() => {
    const countAmount = count;
    console.log(countAmount);

    props.onAddToCart(countAmount);
  }, [count]);

  const addCountPlus = () => {
    setCount((prev) => prev + 1);
  };
  const addCountMinus = () => {
    if (count <= 1) {
      setIsAmountValid(true);
    } else {
      setCount((prev) => prev - 1);
    }
  };

  const addOrder = () => {
    setCount(1);
    setIsAmountValid(false);
  };

  const inputQuantity = isAmountValid ? (
    <button onClick={addOrder}>Добавить</button>
  ) : (
    <div className={styles.inputContainer}>
      <div className={styles.input}>
        <div className={styles.quantityButton} onClick={addCountMinus}>
          <p>-</p>
        </div>
        <div>
          <p className={styles.newInput}>{count}</p>
        </div>
        <div className={styles.quantityButton} onClick={addCountPlus}>
          <p>+</p>
        </div>
      </div>
    </div>
  );

  return (
    <form className={styles.form}>
      <div className={styles.button}>{inputQuantity}</div>
    </form>
  );
}

export default CardButton;
