import { createSlice } from "@reduxjs/toolkit";

const initialState = {
  totalPrice: 0,
  amount: 0,
  items: [],
};

export const basketSlice = createSlice({
  name: "basket",
  initialState,
  reducers: {
    addItems(state, action) {
      state.items.push({ ...action.payload, amount: 0 });
      state.totalPrice = state.items.reduce((sum, obj) => {
        return obj.price + sum;
      }, 0);
    },

    setBasketItemPlus(state, action) {
      state.amount += 1;
      state.items.push({
        ...action.payload,
        amount: 1,
      });
      console.log(state.amount);
    },
    setBasketItemMinus(state) {
      state.amount -= 1;
    },
  },
});

export const { setBasketItemPlus, setBasketItemMinus, addItems } =
  basketSlice.actions;

export default basketSlice.reducer;
