import React from "react";
import TaskList from "../TaskList/TaskList";
import TaskInput from "../TaskInput/TaskInput";

import Styles from "./Task.module.css";

function Task() {
  const [DateTaskList, setDateTaskList] = React.useState([
    {
      id: "0",
      title: "Купить",
      text: "Купить автомобиль",
      time: "2023-05-25",
    },
    {
      id: "1",
      title: "Купить",
      text: "Купить автомобиль",
      time: "2023-05-26",
    },
  ]);

  const deleteProduct = (taskId) => {
    setDateTaskList((prevTasks) => {
      const updatedTaks = prevTasks.filter((task) => task.id !== taskId);
      return updatedTaks;
    });
    //setDateTaskList(product.filter((p) => p !== product));
  };

  const addTaskList = (input) => {
    const newTaskList = [...DateTaskList];
    newTaskList.unshift(input);
    setDateTaskList(newTaskList);
    return;
  };

  return (
    <div className={Styles.content_block}>
      <TaskList items={DateTaskList} onDeleteTask={deleteProduct} />
      <TaskInput addInputList={addTaskList} />
    </div>
  );
}

export default Task;
