import React, { useRef } from "react";
import styles from "./Modal.module.css";
import CartItem from "./CartItem/CartItem";

import zeroBasket from "../../img/BasketZero.png";
import ClearBlack from "../../img/ClearBlack.png";
import Delivery from "./Delivery/Delivery";

function Modal(props) {
  const [state, setState] = React.useState();
  const clickRef = useRef();

  const zeroCart = (
    <div className={styles.basketZero}>
      <img src={zeroBasket} />
      <h3>Корзина пустая, как так? </h3>
    </div>
  );

  console.log(clickRef);

  const click = (e) => {
    console.log(e.path);
  };

  React.useEffect(() => {
    document.body.addEventListener("click", click);
  }, []);

  return (
    <div className={styles.overlay} ref={clickRef}>
      <div className={styles.drawer}>
        <div className={styles.title}>
          <h2>Корзина</h2>
          <img src={ClearBlack} onClick={props.close} />
        </div>
        <div className={styles.item}>
          {zeroCart}
          {/* <CartItem /> */}
          {/* <Delivery /> */}
        </div>
        <div className={styles.CartTotalBlock}>
          <ul>
            <li>
              <span>Итого:</span>
              <div></div>
              <b>2 100 руб.</b>
            </li>
          </ul>
          <button>К оформлению заказ</button>
        </div>
      </div>
    </div>
  );
}

export default Modal;
