import React from "react";
import Header from "./components/Header/Header";
import Content from "./components/Content/Content";

import error from "./img/error.jpg";
import Modal from "./components/Modal/Modal";

function App() {
  const NAV_MENU = [
    { id: "1", name: "Шашлык" },
    { id: "2", name: "Шаурма" },
    { id: "3", name: "Люля-кебаб" },
    { id: "4", name: "Рыба" },
    { id: "5", name: "Горячие блюда" },
    { id: "6", name: "Гарниры" },
    { id: "7", name: "Салаты" },
    { id: "8", name: "Холодные закуски" },
    { id: "9", name: "Выпечка" },
    { id: "10", name: "Напитки" },
  ];
  const DUMMY_MEALS = [
    {
      id: "m1",
      img: error,
      name: "Шаурма на углях",
      description: "Свинана ням ням",
      weight: "130",
      price: "290",
    },
    {
      id: "m2",
      img: error,
      name: "Шашлык",
      description: "Свинана ням ням",
      weight: "130",
      price: "300",
    },
    {
      id: "m3",
      img: error,
      name: "Шашлык",
      description: "Свинана ням ням",
      weight: "130",
      price: "300",
    },
    {
      id: "m4",
      img: error,
      name: "Шашлык",
      description: "Свинана ням ням",
      weight: "130",
      price: "300",
    },
    {
      id: "m5",
      img: error,
      name: "Шашлык",
      description: "Свинана ням ням",
      weight: "130",
      price: "300",
    },
  ];

  const [cartIsVisible, setCartIsVisible] = React.useState(false);

  const headerOpenBasket = () => {
    setCartIsVisible(true);
  };
  const headerCloseBasket = () => {
    setCartIsVisible(false);
  };

  const click = (e) => {
    console.log(e.target);
  };

  document.body.addEventListener("click", click);

  return (
    <>
      <Header open={headerOpenBasket} />
      <Content navigation={NAV_MENU} item={DUMMY_MEALS} />
      {cartIsVisible && <Modal close={headerCloseBasket} />}
    </>
  );
}

export default App;
