import React from "react";
import styles from "./Navigation.module.css";
import { CategoriesItem } from "../../../App";

function Navigation(props) {
  const { categories, setCatigories } = React.useContext(CategoriesItem);
  const navigation = props.navigation.map((item, index) => (
    <li
      key={item.id}
      className={Number(categories) === index ? styles.active : " "}
      onClick={() => setCatigories(item.id)}
    >
      {item.name}
    </li>
  ));

  return (
    <div className={styles.navigastion}>
      <ul>{navigation}</ul>
    </div>
  );
}

export default Navigation;
