import React from "react";
import Header from "./components/Header/Header";
import Content from "./components/Content/Content";

import error from "./img/error.jpg";
import Modal from "./components/Modal/Modal";

const CategoriesItem = React.createContext("");

function App() {
  const NAV_MENU = [
    { id: "0", name: "Популярные" },
    { id: "1", name: "Шашлык" },
    { id: "2", name: "Шаурма" },
    { id: "3", name: "Люля-кебаб" },
    { id: "4", name: "Рыба" },
    { id: "5", name: "Горячие блюда" },
    { id: "6", name: "Гарниры" },
    { id: "7", name: "Салаты" },
    { id: "8", name: "Холодные закуски" },
    { id: "9", name: "Выпечка" },
    { id: "10", name: "Напитки" },
  ];

  const [items, setItems] = React.useState([]);
  const [isLoading, setIsLoading] = React.useState(true);
  const [cartIsVisible, setCartIsVisible] = React.useState(false);
  const [categories, setCatigories] = React.useContext(0);

  React.useEffect(() => {
    setIsLoading(true);
    fetch(
      "https://648a28de5fa58521cab0f109.mockapi.io/items?category=" + categories
    )
      .then((res) => res.json())
      .then((arr) => {
        setItems(arr);
        setIsLoading(false);
      });
  }, [categories]);

  const onSelectItem = (item) => {
    setCatigories(item);
  };

  return (
    <>
      <CategoriesItem.Provider value={[categories, setCatigories]}>
        <Header open={() => setCartIsVisible(true)} />
        <Content
          navigation={NAV_MENU}
          item={items}
          isLoading={isLoading}
          // value={categories}
          // onClickCategory={(id) => onSelectItem(id)}
        />
        {cartIsVisible && <Modal close={() => setCartIsVisible(false)} />}
      </CategoriesItem.Provider>
    </>
  );
}

export default App;
